SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_sales_facturacion,public;




CREATE OR REPLACE PROCEDURE eliseo.pkg_sales_facturacion_sp_venta_electronica (P_ID_VENTA bigint) AS $body$
DECLARE

        L_ID_ENTIDAD bigint;
        L_TIPOEMISORID varchar(20);


BEGIN  

        SELECT 
                ID_ENTIDAD INTO STRICT L_ID_ENTIDAD
        FROM VENTA
        WHERE ID_VENTA = P_ID_VENTA;

        SELECT TIPOEMISORID INTO STRICT L_TIPOEMISORID
        FROM CONTA_ENTIDAD A, CONTA_EMPRESA B
        WHERE A.ID_EMPRESA = B.ID_EMPRESA
        AND A.ID_ENTIDAD = L_ID_ENTIDAD;


        IF L_TIPOEMISORID = 'NUBE' THEN

            CALL ELISEO.pkg_sales_facturacion_sp_venta_electronica_nube(P_ID_VENTA);

        ELSIF L_TIPOEMISORID = 'UPEU' THEN  --UPEU -OTRO SERVER EFAC
            CALL ELISEO.pkg_sales_facturacion_sp_venta_electronica_upeu(P_ID_VENTA);

        ELSIF L_TIPOEMISORID = 'DESIS' THEN  --UPEU -OTRO SERVER EFAC
            CALL ELISEO.pkg_sales_facturacion_desis_sp_venta_electronica_desis(P_ID_VENTA);

        ELSIF L_TIPOEMISORID = 'BIZLINKS' THEN  -- UPS BIZLINKS
            CALL ELISEO.pkg_sales_facturacion_bizlinks_sp_venta_electronica(P_ID_VENTA);

        ELSE
            null;

        END IF;

    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
