SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_sales_school,public;




CREATE OR REPLACE PROCEDURE eliseo.pkg_sales_school_sp_serv_creact_nota (P_ID_ENTIDAD bigint,P_ID_DEPTO text,P_ID_ANHO bigint,P_ID_MES bigint,P_ID_VENTA bigint --,P_ID_VDETALLE_REF numeric
 ,P_ID_ARTICULO bigint, P_ID_PERSONA bigint,P_ID_CLIENTE bigint, P_ID_COMPROBANTE text,P_ID_TIPONOTA text,P_ID_TIPOTRANSACCION bigint,P_ID_DINAMICA bigint,P_PRECIO bigint, P_ID_MONEDA bigint,  -- moneda de venta 
 P_GLOSA text,P_ID_NOTA OUT bigint,P_ERROR OUT bigint,P_MSGERROR out text) AS $body$
DECLARE


        l_serie varchar(10);
        l_correlativo varchar(10);
        l_numero varchar(10);

        l_igv decimal(10,2);
        l_tc decimal(10,3);
        l_id_moneda_tc bigint;
        l_automatico varchar(2);
        l_id_voucher bigint;
        l_contar bigint;
        L_DOCUMENTO bigint :=0;
        L_ID_SUCURSAL bigint;
        L_ID_MONEDA bigint;
        --L_TOTAL numeric;
        --L_TOTAL_ME numeric;
        L_ID_TIPOTRANSACCION bigint;
        L_ID_DINAMICA bigint;

        -- L_MONTO numeric;
        L_SALDO bigint;

        l_id_tipoigv varchar(10);
        l_gravado varchar(10);

        l_totaldescuento bigint;
        l_precio bigint;
        l_precio_base bigint;
        l_importe bigint;
        l_base bigint;
        l_igv_item bigint;
        l_dc varchar(3);

        l_descuento bigint;
        l_precio_me bigint;
        l_precio_base_me bigint;
        --l_precio_alm_me numeric;
        l_base_me bigint;
        l_igv_me bigint;
        l_descuento_me bigint;
        l_importe_me bigint;

        L_ID_PERSONA_V bigint;
        l_id_leyenda varchar(10):='1000';
        --l_id_voucher numeric;
        
        -- REF
        L_ID_COMPROBANTE_REF varchar(10);
        L_SERIE_REF varchar(25);
        L_NUMERO_REF varchar(25);
        L_ID_CLIENTE_LEGAL_REF bigint;

        l_id_nota bigint;
        l_error bigint;
        l_msgerror varchar(200):='';
        l_tipoasiento_ventas varchar(5) := 'RV';

        
BEGIN
        
            if P_ID_ENTIDAD = 7124 and P_ID_DEPTO = '7' then 
                l_tipoasiento_ventas := 'RVL';
            end if;

            if P_ID_ENTIDAD = 7124 and P_ID_DEPTO = '4' then
                l_tipoasiento_ventas := 'RVC';
            end if;

            L_ID_MONEDA := P_ID_MONEDA;
            l_id_moneda_tc:=9;

            l_error :=0;
            -- DELETE VENTA_DETALLE WHERE ID_VENTA IN (SELECT ID_VENTA FROM VENTA WHERE ID_ENTIDAD = P_ID_ENTIDAD AND ID_DEPTO = P_ID_DEPTO AND ID_PERSONA = P_ID_PERSONA AND ID_COMPROBANTE IN ('07','08') AND ESTADO = 0);
            DELETE FROM VENTA_DETALLE WHERE ID_VENTA IN (
			SELECT V.ID_VENTA FROM VENTA V
			INNER JOIN ELISEO.TIPO_COMPROBANTE TC ON V.ID_COMPROBANTE = TC.ID_COMPROBANTE 
			WHERE V.ID_ENTIDAD = P_ID_ENTIDAD AND V.ID_DEPTO = P_ID_DEPTO 
			AND V.ID_PERSONA = P_ID_PERSONA AND 
			TC.TIPO = 'N'
			AND V.ESTADO = 0);--Agregado por Ulices
            --DELETE VENTA WHERE ID_ENTIDAD = P_ID_ENTIDAD AND ID_DEPTO = P_ID_DEPTO AND ID_PERSONA = P_ID_PERSONA AND ID_COMPROBANTE IN ('07','08') AND ESTADO = 0;
            DELETE FROM VENTA WHERE ID_VENTA IN (
			SELECT V.ID_VENTA FROM VENTA V
			INNER JOIN ELISEO.TIPO_COMPROBANTE TC ON V.ID_COMPROBANTE = TC.ID_COMPROBANTE
			WHERE V.ID_ENTIDAD = P_ID_ENTIDAD AND V.ID_DEPTO = P_ID_DEPTO 
			AND V.ID_PERSONA = P_ID_PERSONA AND 
			TC.TIPO = 'N'
			AND V.ESTADO = 0);--Agregado por Ulices
            SELECT ID_COMPROBANTE, SERIE, NUMERO, ID_SUCURSAL,ID_MONEDA, TOTAL, ID_CLIENTE_LEGAL
            INTO STRICT L_ID_COMPROBANTE_REF, L_SERIE_REF, L_NUMERO_REF, L_ID_SUCURSAL,L_ID_MONEDA, L_SALDO, L_ID_CLIENTE_LEGAL_REF
            FROM VENTA WHERE ID_VENTA=P_ID_VENTA;


            --obtiene IGV de la fecha actual
            --select FC_IGV(CURRENT_DATE ) into l_igv from dual;
           
	       	-- Obtiene el impuesto de la fecha actual   --agregado por Ulices
	        select ELISEO.FC_IMPUESTO(P_ID_ENTIDAD, CURRENT_DATE) into STRICT l_igv;   --agregado por Ulices
            --obtiene tipo de cambio del dia
            select FC_TIPOCAMBIO(l_id_moneda_tc,CURRENT_DATE,'V' ) into STRICT l_tc;

            if coalesce(l_tc::text, '') = '' then
                l_tc:=0;
            end if;

            /*
            l_contar := pkg_sales_fc_verificar_serie_numero(P_ID_PERSONA,'03',P_ID_ENTIDAD, P_ID_DEPTO);
            if l_contar < 1 then
                l_error := 1;
                l_msgerror := 'XAlto! No existe un punto de impresión activo de notas para el usuario. [tipo de documento: 03]'||P_ID_PERSONA;
--                 GOTO salida_rapida;
            elsif l_contar > 1 then
                l_error := 1;
                l_msgerror := 'XAlto! Existe mas de un punto de impresión activo de notas para el usuario. [tipo de documento: 03]'||P_ID_PERSONA;
--                 GOTO salida_rapida;
            END IF;
            */
            /*
            SELECT TOTAL,TOTAL_ME INTO L_TOTAL,L_TOTAL_ME
                FROM VW_SALES_SALDO
                WHERE ID_VENTA = P_ID_VENTA;
            IF L_ID_MONEDA = 7 THEN
                L_MONTO := (1*P_PRECIO);
                L_SALDO := L_TOTAL;
            ELSE
                L_MONTO := (1*P_PRECIO)/l_tc;
                L_SALDO := L_TOTAL_ME;
            END IF;
            */
           SELECT DC INTO STRICT l_dc FROM TIPO_COMPROBANTE WHERE ID_COMPROBANTE = P_ID_COMPROBANTE; --Agregado por Ulices
           L_ID_TIPOTRANSACCION := P_ID_TIPOTRANSACCION;--agregado por Ulices
           L_ID_DINAMICA := P_ID_DINAMICA;--agregado por Ulices
           
            /* IF P_ID_COMPROBANTE = '07' THEN -- Solo cuando es NC la dinàmica puede cambiar.
                L_ID_TIPOTRANSACCION := P_ID_TIPOTRANSACCION;
                L_ID_DINAMICA := P_ID_DINAMICA;
                l_dc := 'C';
            ELSE
                l_dc := 'D';
                L_ID_TIPOTRANSACCION := P_ID_TIPOTRANSACCION;
                L_ID_DINAMICA := P_ID_DINAMICA;
                
                -- SELECT ID_TIPOTRANSACCION INTO L_ID_TIPOTRANSACCION FROM VENTA WHERE ID_VENTA=P_ID_VENTA;
                -- SELECT ID_DINAMICA INTO L_ID_DINAMICA FROM VENTA_DETALLE
                --                                     WHERE ID_VENTA=P_ID_VENTA AND ID_ARTICULO = P_ID_ARTICULO;
            END IF;*/
             
            -- IF P_ID_COMPROBANTE = '07' AND P_PRECIO > L_SALDO THEN
            IF l_dc = 'C' AND P_PRECIO > L_SALDO THEN  --agregado por Ulices
                l_error :=1;
                l_msgerror := 'Alto! El importe de la nota de crédito es mayor al importe del documento principal.';
--                 goto salida_rapida;
            END IF;
            l_contar := pkg_sales_fc_verificar_serie_numero(P_ID_PERSONA,P_ID_COMPROBANTE,P_ID_ENTIDAD, P_ID_DEPTO);
            if l_contar < 1 then
                l_error := 1;
                l_msgerror := 'Alto! No existe un punto de impresión activo de transferencias para el usuario. [tipo de documento: '||P_ID_COMPROBANTE||']';
--                 GOTO salida_rapida;
            elsif l_contar > 1 then
                l_error := 1;
                l_msgerror := 'Alto! Existe mas de un punto de impresión activo de transferencias para el usuario. [tipo de documento: '||P_ID_COMPROBANTE||']';
--                 GOTO salida_rapida;
            END IF;

            /*
            SELECT MAX(A.ID_PERSONA) INTO L_ID_PERSONA_V
            FROM APS_EMPLEADO A 
                INNER JOIN APS_TRABAJADOR B ON A.ID_PERSONA = B.ID_PERSONA
                INNER JOIN APS_CARGO C ON B.ID_CARGO = C.ID_CARGO
                INNER JOIN CONTA_ENTIDAD E ON A.ID_ENTIDAD = E.ID_ENTIDAD
            WHERE A.ID_ENTIDAD = P_ID_ENTIDAD
            AND (
            	(A.ID_DEPTO LIKE P_ID_DEPTO||'%' AND E.ID_TIPOENTIDAD = 12)
            	OR (E.ID_TIPOENTIDAD != 12)
            )
            AND UPPER(C.NOMBRE) LIKE '%CONTADOR%'
            AND A.ESTADO = 'A';
            
            */
            
            L_ID_PERSONA_V := P_ID_PERSONA;

            if coalesce(L_ID_PERSONA_V::text, '') = '' then
                l_error:=1;
                l_msgerror:='Alto: No existe asignado un contador para la entidad: ' || P_ID_ENTIDAD || ' y el departamento: ' || P_ID_DEPTO;
--                 GOTO salida_rapida;
            end if;

            SELECT  count(*) into STRICT l_contar FROM CONTA_VOUCHER_CONFIG
                WHERE ID_ENTIDAD = P_ID_ENTIDAD
                AND ID_DEPTO =P_ID_DEPTO
                AND ID_MODULO = '13' -- modulo ventas
                AND ID_ANHO = P_ID_ANHO
                and ID_TIPOASIENTO=l_tipoasiento_ventas
                AND AUTOMATICO='S'
                AND ID_TIPOVOUCHER = 1;

            if l_contar < 1 then
                l_error:=1;
                l_msgerror:='Alto: No existe la configuración de vouchers para las notas, del periodo '||P_ID_ANHO||'-'||l_tipoasiento_ventas::text;
--                 GOTO salida_rapida;
            elsif l_contar > 1 then
                l_error:=1;
                l_msgerror:='Alto: Existe más de una configuración de vouchers para las notas, del periodo '||P_ID_ANHO::text;
--                 GOTO salida_rapida;
            end if;

            
            IF l_error = 0 THEN
                pkg_sales_school_sp_obtener_serie_numero(P_ID_PERSONA,P_ID_COMPROBANTE,P_ID_ENTIDAD
                ,P_ID_DEPTO
                ,l_serie,l_correlativo);

                CALL pkg_accounting_sp_crear_voucher(P_ID_ENTIDAD,P_ID_DEPTO,P_ID_ANHO,P_ID_MES,clock_timestamp(),l_tipoasiento_ventas,1,'','S',L_ID_PERSONA_V,l_id_voucher);

                select lpad(l_correlativo::text,8,'0') into STRICT l_numero;

                INSERT INTO VENTA(
                        ID_PARENT,ID_ENTIDAD,ID_DEPTO,ID_ANHO,ID_MES,ID_PERSONA,
                        ID_CLIENTE,ID_SUCURSAL, ID_COMPROBANTE,ID_TIPONOTA, ID_IGV, 
                        ID_MONEDA, ID_LEYENDA, ID_TIPOTRANSACCION,TIPOCAMBIO, SERIE, NUMERO, FECHA, 
                        GLOSA,ESTADO,ID_COMPROBANTE_REF, SERIE_REF, NUMERO_REF, ID_VOUCHER,
                        ID_CLIENTE_LEGAL
                )VALUES (
                        P_ID_VENTA, P_ID_ENTIDAD, P_ID_DEPTO, P_ID_ANHO, P_ID_MES, P_ID_PERSONA,
                        P_ID_CLIENTE, L_ID_SUCURSAL,P_ID_COMPROBANTE, P_ID_TIPONOTA, l_igv,
                        L_ID_MONEDA, l_id_leyenda,L_ID_TIPOTRANSACCION,l_tc,l_serie, l_numero,clock_timestamp(),
                        P_GLOSA,1, L_ID_COMPROBANTE_REF,L_SERIE_REF, L_NUMERO_REF, l_id_voucher,
                        L_ID_CLIENTE_LEGAL_REF
                ) returning ID_VENTA INTO l_id_nota;

                select ID_TIPOIGV into STRICT l_id_tipoigv from CONTA_DINAMICA where ID_DINAMICA=L_ID_DINAMICA;
                select GRAVADO into STRICT l_gravado from TIPO_IGV where ID_TIPOIGV=l_id_tipoigv;

                l_igv:=l_igv/100;
                l_totaldescuento:=0;--l_descuento*P_CANTIDAD;
                l_precio:=P_PRECIO;
                l_precio_base:=l_precio;
                l_importe:=(l_precio*1)-(l_totaldescuento);
                --l_precio_alm:=l_precio_alm;
                l_base:=l_importe;
                l_igv_item:=0;
                if l_gravado in ('G') then
                    l_precio_base:=l_precio/(1+l_igv);
                    l_base:=l_importe/(1+l_igv);
                    l_igv_item:=l_importe-l_base;
                end if;
                l_descuento:=l_totaldescuento;
                l_precio_me:=0;
                l_precio_base_me:=0;
                --l_precio_alm_me:=0;
                l_base_me:=0;
                l_igv_me:=0;
                l_descuento_me:=0;
                l_importe_me:=0;
              
                if l_id_moneda=9 then
                    l_totaldescuento:=l_descuento*1;
                    l_totaldescuento:=l_totaldescuento/l_tc;
                    l_precio_me:=l_precio/l_tc;
                    l_precio_base_me:=l_precio_base/l_tc;
                    l_importe_me:=l_importe/l_tc;
                    --l_precio_alm_me:=l_precio_alm/l_tc;
                    l_base_me:=l_base/l_tc;
                    l_igv_me:=l_igv_item/l_tc;
                    l_descuento_me:=l_totaldescuento;
                end if;

                INSERT INTO VENTA_DETALLE(
                        ID_VENTA,ID_TIPOIGV,ID_DINAMICA, DETALLE, CANTIDAD, PRECIO,  PRECIO_BASE, 
                        BASE, IGV, DESCUENTO, IMPORTE, PRECIO_ME, PRECIO_BASE_ME, 
                        BASE_ME, IGV_ME, DESCUENTO_ME, IMPORTE_ME, ID_ARTICULO, DC
                        --,ID_VDETALLE_REF
                )VALUES (
                        l_id_nota,l_id_tipoigv,L_ID_DINAMICA,P_GLOSA, 1,l_precio,l_precio_base,
                        l_base,l_igv_item, l_descuento,l_importe,l_precio_me,l_precio_base_me,
                        l_base_me,l_igv_me, l_descuento_me,l_importe_me, P_ID_ARTICULO, l_dc
                        --,P_ID_VDETALLE_REF
                );

                -- pkg_sales_sp_generar_asiento(l_id_nota,l_error,l_msgerror);
                CALL pkg_sales_sp_actualizar_total_venta(l_id_nota);
                CALL pkg_sales_sp_serv_genasiento_nota(l_id_nota,l_error,l_msgerror);
                CALL pkg_sales_facturacion_sp_venta_electronica(l_id_nota);
                
          END IF;

--         <<salida_rapida>>
        P_ID_NOTA:=l_id_nota;
        P_ERROR:=l_error;
        P_MSGERROR:=l_msgerror;

    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE eliseo.pkg_sales_school_sp_serv_creact_nota (P_ID_ENTIDAD bigint,P_ID_DEPTO text,P_ID_ANHO bigint,P_ID_MES bigint,P_ID_VENTA bigint  ,P_ID_ARTICULO bigint, P_ID_PERSONA bigint,P_ID_CLIENTE bigint, P_ID_COMPROBANTE text,P_ID_TIPONOTA text,P_ID_TIPOTRANSACCION bigint,P_ID_DINAMICA bigint,P_PRECIO bigint, P_ID_MONEDA bigint, P_GLOSA text,P_ID_NOTA OUT bigint,P_ERROR OUT bigint,P_MSGERROR out text) FROM PUBLIC;
