CREATE OR REPLACE PROCEDURE eliseo.pkg_setup_sp_process_execute_paso (P_ID_EXECUTE bigint,P_ID_PERSONA bigint,P_ID_PASO_ACTUAL bigint,P_ID_PASO_NEXT bigint,P_DETALLE text,P_ESTADO text,P_ERROR OUT bigint,P_MSGERROR OUT text) AS $body$
DECLARE

        	L_ERROR bigint:=0;
	        L_MSGERROR varchar(200):='';
	        L_CONTAR bigint:=0;
	
        
BEGIN
                
            SELECT COUNT(1) INTO STRICT L_CONTAR FROM LAMB_PROCESS_EXECUTE WHERE ID_EXECUTE = P_ID_EXECUTE;

            IF L_CONTAR > 0 THEN
                INSERT INTO LAMB_PROCESS_EXEC_PASO(ID_EXECUTE,ID_PERSONA,ID_PASO_ACTUAL,ID_PASO_NEXT,DETALLE,FECHA,ESTADO)
                VALUES (P_ID_EXECUTE,P_ID_PERSONA,P_ID_PASO_ACTUAL,P_ID_PASO_NEXT,P_DETALLE,clock_timestamp(),P_ESTADO);

                UPDATE LAMB_PROCESS_EXECUTE SET ID_PASO_ACTUAL = P_ID_PASO_ACTUAL WHERE ID_EXECUTE = P_ID_EXECUTE;
            ELSE
                L_ERROR := 0;
                L_MSGERROR := 'ALTO: No existe proceso';
            END IF;

	       P_ERROR:= L_ERROR;
	       P_MSGERROR:= L_MSGERROR;
    END;
$body$
