SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_sire,public;




CREATE OR REPLACE FUNCTION eliseo.pkg_sire_fc_cliente_numdoc (P_NUMDOC_CLIENTE text) RETURNS bigint AS $body$
DECLARE

        L_ID_PERSONA bigint;

BEGIN
        SELECT ID_PERSONA INTO STRICT L_ID_PERSONA FROM (
        	SELECT  
                ID_PERSONA
                FROM MOISES.VW_PERSONA_NATURAL
                WHERE NUM_DOCUMENTO=P_NUMDOC_CLIENTE
                AND ID_TIPODOCUMENTO NOT IN (97,98) --DOCUMENTOS DE AFP - ONP
 
                
UNION
 
                SELECT 
                    ID_PERSONA
                FROM MOISES.VW_PERSONA_JURIDICA 
                WHERE ID_RUC=P_NUMDOC_CLIENTE
         ) X LIMIT 1;

        RETURN(L_ID_PERSONA);
   END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
