CREATE OR REPLACE FUNCTION eliseo.utils_convert_to_char_ (P_EXPR TS , P_PRECISION bigint DEFAULT 0, P_SCALE bigint DEFAULT 0, P_STYLE bigint DEFAULT NULL) RETURNS char AS $body$
DECLARE

V_FORMAT varchar(100);
V_RESULT varchar(4000);

BEGIN
  IF (P_STYLE IS NOT NULL AND P_STYLE::text <> '') THEN
    V_FORMAT := utils_get_format_from_style(P_STYLE);
    v_result := TO_CHAR(P_EXPR,V_FORMAT);
  ELSE
    v_result := P_EXPR::text;
  END IF;
  RETURN utils_substr_(v_result,P_PRECISION);
END;
