CREATE OR REPLACE FUNCTION eliseo.utils_isnumeric (p_expr text) RETURNS bigint AS $body$
DECLARE

    numeric_val bigint;
    temp_str varchar(50);

BEGIN
    temp_str := p_expr;
    IF SUBSTR(temp_str, 1, 1) = '$' THEN
       temp_str := SUBSTR(temp_str, 2);
    END IF;

    numeric_val := (temp_str)::numeric;
    RETURN 1;
EXCEPTION
    WHEN OTHERS THEN
       RETURN 0;
END;
$body$
