SET client_encoding TO 'UTF8';
SET search_path = eliseo,utils,public;




CREATE OR REPLACE FUNCTION eliseo.utils_object_id (objectref text) RETURNS integer AS $body$
DECLARE

  ownerName  varchar(128);
  objectName varchar(128);
  objectId   integer;

BEGIN
  --select * from all_objects;
  --select utils_object_id('system.redo_log') from dual;
  IF ( position('.' in objectref) = 0 )THEN
    ownerName             := NULL;
    objectName            := objectref;
  ELSE
    ownerName  := SUBSTR(objectref,0,position('.' in objectref)-1);
    objectName := SUBSTR(objectref,position('.' in objectref)  +1);
  END IF;
  BEGIN
    SELECT object_id
    INTO STRICT objectId
    FROM all_objects
    WHERE UPPER(owner)     = UPPER(coalesce(ownerName,owner))
    AND  UPPER(object_name) = UPPER(objectName);
  EXCEPTION WHEN OTHERS THEN objectId := NULL;
  END;
  RETURN objectId;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
