CREATE OR REPLACE FUNCTION eliseo.utils_stats_date (p_table text, p_index text) RETURNS timestamp(0) AS $body$
DECLARE

    v_last_analyzed timestamp(0);

BEGIN
    SELECT last_analyzed INTO STRICT v_last_analyzed
      FROM USER_IND_STATISTICS
     WHERE table_name LIKE UPPER(p_table)
       AND index_name LIKE UPPER(p_index);

    RETURN v_last_analyzed;
EXCEPTION
    WHEN OTHERS THEN
       RAISE EXCEPTION '%', DBMS_UTILITY.FORMAT_ERROR_STACK USING ERRCODE = '45000';
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
