CREATE OR REPLACE PROCEDURE eliseo.iudp_fondo (P_Modo integer, P_ID_FONDO text, P_ID_PARENT text, P_NOMBRE text) AS $body$
DECLARE


  S_ID_FONDO            bigint;
  S_ID_PARENT           bigint;
  S_CANTIDAD            integer;

 
BEGIN
	 
IF P_Modo = 0 THEN
    

    S_ID_FONDO :=  TO_NUMBER(P_ID_FONDO, '9999999');
    S_ID_PARENT := CASE WHEN P_ID_PARENT IN ('@ParentCode@','') THEN NULL ELSE TO_NUMBER(P_ID_PARENT, '9999999') END;

    SELECT COUNT(*) INTO STRICT S_CANTIDAD FROM Conta_Fondo WHERE ID_FONDO = P_ID_FONDO;
    
    IF S_CANTIDAD = 0 THEN
    
      INSERT INTO Conta_Fondo(ID_FONDO, ID_PARENT, NOMBRE, ES_GRUPO)
        VALUES (S_ID_FONDO, S_ID_PARENT, P_NOMBRE, '0');

    ELSE
      UPDATE Conta_Fondo SET
        NOMBRE = P_NOMBRE
      WHERE ID_FONDO = S_ID_FONDO;
    END IF;
    
    UPDATE Conta_Fondo SET
      ES_GRUPO = '1'
    WHERE ID_FONDO = S_ID_PARENT;
  END IF;


 end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
