CREATE OR REPLACE PROCEDURE eliseo.iudp_planilla ( v_Modo bigint, v_ID_ENTIDAD bigint, v_ID_ANHO bigint, v_ID_MES bigint, v_ID_CONTRATO bigint DEFAULT NULL, v_ID_DEPARTAMENTO text DEFAULT NULL, v_COD_TIPOSTATUS text DEFAULT NULL, iv_ID_SISTEMAPENSION bigint DEFAULT NULL, v_ES_MISIONERO bigint DEFAULT NULL, v_ES_CONFIANZA bigint DEFAULT NULL, v_NUM_DIAS bigint DEFAULT NULL, v_NUM_HORAS bigint DEFAULT NULL, v_NUM_HORASEXTRA bigint DEFAULT NULL, v_NUM_DIASVAC bigint DEFAULT NULL, v_NOM_CARGO text DEFAULT NULL, v_FMR bigint DEFAULT NULL, v_ID_CARGO bigint DEFAULT NULL) AS $body$
DECLARE

  v_ID_SISTEMAPENSION bigint := iv_ID_SISTEMAPENSION;
   l_contar bigint;

BEGIN
  IF v_Modo = 1 THEN
    BEGIN
      DELETE FROM Aps_Planilla_Detalle
      WHERE ID_ENTIDAD = v_ID_ENTIDAD
      AND ID_ANHO      = v_ID_ANHO
      AND v_ID_MES    IN ( -1,ID_MES );

      --DELETE Aps_Planilla
      --WHERE ID_ENTIDAD = v_ID_ENTIDAD
      --AND ID_ANHO      = v_ID_ANHO
      --AND v_ID_MES    IN ( -1,ID_MES ) ;
      
    END;
  ELSE
    DECLARE
      v_ID_PERSONA     bigint := 0;
      v_ID_TIPOESTATUS bigint;
    BEGIN
      SELECT MAX(ID_PERSONA)
      INTO STRICT v_ID_PERSONA
      FROM Aps_Empleado
      WHERE ID_ENTIDAD = v_ID_ENTIDAD
      AND ID_CONTRATO  = v_ID_CONTRATO;

      IF v_ID_PERSONA  > 0 THEN
        DECLARE
          v_temp smallint := 0;
        BEGIN
          SELECT max(ID_TIPOESTATUS)
          INTO STRICT v_ID_TIPOESTATUS
          FROM Tipo_Estatus
          WHERE SIGLAS = v_COD_TIPOSTATUS;

          SELECT coalesce(max(ID_SISTEMAPENSION),0)
          INTO STRICT v_ID_SISTEMAPENSION
          FROM Aps_Sistema_Pension
          WHERE COD_APS = v_ID_SISTEMAPENSION;
          BEGIN
            SELECT 1
            INTO STRICT v_temp

            WHERE NOT EXISTS (SELECT ID_PERSONA
              FROM Aps_Planilla
              WHERE ID_ENTIDAD = v_ID_ENTIDAD
              AND ID_ANHO      = v_ID_ANHO
              AND ID_MES       = v_ID_MES
              AND ID_PERSONA   = v_ID_PERSONA
              AND ID_CONTRATO  = v_ID_CONTRATO 
               LIMIT 1);
          EXCEPTION
          WHEN OTHERS THEN
            NULL;
          END;
          IF v_temp = 1 THEN
            INSERT
            INTO Aps_Planilla(
                ID_ENTIDAD,
                ID_ANHO,
                ID_MES,
                ID_PERSONA,
                ID_CONTRATO,
                ID_DEPTO,
                ID_TIPOESTATUS,
                ID_SISTEMAPENSION,
                ES_MISIONERO,
                ES_CONFIANZA,
                NUM_DIAS,
                NUM_HORAS,
                NUM_HORASEXTRA,
                NUM_DIASVAC,
                NOM_CARGO,
                FMR,
                ID_CARGO
              )
              VALUES (
                v_ID_ENTIDAD,
                v_ID_ANHO,
                v_ID_MES,
                v_ID_PERSONA,
                v_ID_CONTRATO,
                v_ID_DEPARTAMENTO,
                v_ID_TIPOESTATUS,
                NULLIF(v_ID_SISTEMAPENSION, 0),
                v_ES_MISIONERO,
                v_ES_CONFIANZA,
                v_NUM_DIAS,
                v_NUM_HORAS,
                v_NUM_HORASEXTRA,
                v_NUM_DIASVAC,
                v_NOM_CARGO,
                v_FMR,
                v_ID_CARGO
              );
          ELSE
            UPDATE Aps_Planilla
            SET ID_DEPTO = v_ID_DEPARTAMENTO,
              ID_TIPOESTATUS    = v_ID_TIPOESTATUS,
              ID_SISTEMAPENSION  = NULLIF(v_ID_SISTEMAPENSION, 0),
              ES_MISIONERO      = coalesce(v_ES_MISIONERO, 0),
              ES_CONFIANZA      = coalesce(v_ES_CONFIANZA, 0),
              NUM_DIAS          = coalesce(NULLIF(v_NUM_DIAS, 0), NUM_DIAS),
              NUM_HORAS         = coalesce(NULLIF(v_NUM_HORAS, 0), NUM_HORAS),
              NUM_HORASEXTRA    = coalesce(NULLIF(v_NUM_HORASEXTRA, 0), NUM_HORASEXTRA),
              NUM_DIASVAC       = coalesce(NULLIF(v_NUM_DIASVAC, 0), NUM_DIASVAC),
              NOM_CARGO         = coalesce(NULLIF(v_NOM_CARGO, ' '), NOM_CARGO),
              FMR               = v_FMR,
              ID_CARGO          = v_ID_CARGO
            WHERE ID_ENTIDAD    = v_ID_ENTIDAD
            AND ID_ANHO         = v_ID_ANHO
            AND ID_MES          = v_ID_MES
            AND ID_PERSONA      = v_ID_PERSONA
            AND ID_CONTRATO     = v_ID_CONTRATO;
          END IF;

          select count(*) into STRICT l_contar from aps_cargo where id_cargo=v_ID_CARGO;
          if l_contar=0 then
            insert into aps_cargo(id_cargo,nombre,estado) values (v_ID_CARGO,v_NOM_CARGO,'1');
          else
            update aps_cargo set nombre = v_NOM_CARGO, estado = '1' where id_cargo = v_ID_CARGO;
          end if;
            update aps_trabajador set id_cargo=v_ID_CARGO where id_persona=v_ID_PERSONA;
        END;
      END IF;
    END;
  END IF;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE eliseo.iudp_planilla ( v_Modo bigint, v_ID_ENTIDAD bigint, v_ID_ANHO bigint, v_ID_MES bigint, v_ID_CONTRATO bigint DEFAULT NULL, v_ID_DEPARTAMENTO text DEFAULT NULL, v_COD_TIPOSTATUS text DEFAULT NULL, iv_ID_SISTEMAPENSION bigint DEFAULT NULL, v_ES_MISIONERO bigint DEFAULT NULL, v_ES_CONFIANZA bigint DEFAULT NULL, v_NUM_DIAS bigint DEFAULT NULL, v_NUM_HORAS bigint DEFAULT NULL, v_NUM_HORASEXTRA bigint DEFAULT NULL, v_NUM_DIASVAC bigint DEFAULT NULL, v_NOM_CARGO text DEFAULT NULL, v_FMR bigint DEFAULT NULL, v_ID_CARGO bigint DEFAULT NULL) FROM PUBLIC;

