CREATE OR REPLACE PROCEDURE eliseo.iudp_restriccion (P_Modo integer, P_ID_RESTRICCION text, P_ID_PARENT text, P_NOMBRE text) AS $body$
DECLARE


  S_ID_PARENT           varchar(60);
  S_CANTIDAD            integer;

 
BEGIN
	 
IF P_Modo = 0 THEN
    S_ID_PARENT := CASE WHEN P_ID_PARENT IN ('@ParentCode@') THEN '' ELSE P_ID_PARENT END;

    SELECT COUNT(*) INTO STRICT S_CANTIDAD FROM Conta_Restriccion WHERE ID_RESTRICCION = P_ID_RESTRICCION;
    
    IF S_CANTIDAD = 0 THEN
    
      INSERT INTO Conta_Restriccion(ID_RESTRICCION, ID_PARENT, NOMBRE, ES_GRUPO)
        VALUES (P_ID_RESTRICCION, NULLIF(S_ID_PARENT, ''), P_NOMBRE, 0);

    ELSE
      UPDATE Conta_Restriccion SET
        NOMBRE = P_NOMBRE
      WHERE ID_RESTRICCION = P_ID_RESTRICCION;
    END IF;
    
    UPDATE Conta_Restriccion SET
      ES_GRUPO = 1
    WHERE ID_RESTRICCION = S_ID_PARENT;
  END IF;


 end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
