CREATE OR REPLACE PROCEDURE eliseo.iudp_tipo_activo_movimiento (P_Modo integer, P_ID_TIPOACTIVOMOVIMIENTO text, P_NOMBRE text, P_ES_ACREEDORA text) AS $body$
DECLARE


  S_CANTIDAD            integer;

 
BEGIN
	 
IF P_Modo = 0 THEN
    
    SELECT COUNT(*) INTO STRICT S_CANTIDAD FROM Tipo_Activo_Movimiento WHERE ID_TIPOACTIVOMOVIMIENTO = P_ID_TIPOACTIVOMOVIMIENTO;
    
    IF S_CANTIDAD = 0 THEN
    
      INSERT INTO Tipo_Activo_Movimiento(ID_TIPOACTIVOMOVIMIENTO, NOMBRE, ES_ACREEDORA)
        VALUES (P_ID_TIPOACTIVOMOVIMIENTO, P_NOMBRE, 0);

    ELSE
      UPDATE Tipo_Activo_Movimiento SET
        NOMBRE = P_NOMBRE
      WHERE ID_TIPOACTIVOMOVIMIENTO = P_ID_TIPOACTIVOMOVIMIENTO;
    END IF;

  END IF;


 end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
