CREATE OR REPLACE PROCEDURE eliseo.scp_dinamic_create_adds ( v_ID_ENTIDAD bigint, v_ID_DEPTO text DEFAULT NULL) AS $body$
DECLARE


	
	v_d_a_cursor REFCURSOR;
	d_a_record      CONTA_DINAMICA_ASIENTO%ROWTYPE;
	v_d_a_stmt_str      varchar(200);

	
	v_d_cc_cursor REFCURSOR;
	d_cc_record      CONTA_DINAMICA_CTA_CTE%ROWTYPE;
	v_d_cc_stmt_str      varchar(200);
	
	
	v_d_d_cursor REFCURSOR;
	d_d_record      CONTA_DINAMICA_DEPTO%ROWTYPE;
	v_d_d_stmt_str      varchar(200);
	
   l_id_dinamica bigint;
   l_id_asiento bigint;

    c1 CURSOR FOR
	SELECT * FROM conta_dinamica
	WHERE ID_ENTIDAD = 7222
	AND ID_DEPTO = '121212'
	AND ID_ANHO = 2019
  AND nombre like 'Pagos a Proveedores'
  order by ID_DINAMICA;
	
BEGIN

	SELECT max(id_dinamica) INTO STRICT l_id_dinamica FROM conta_dinamica;
	SELECT max(id_asiento) INTO STRICT l_id_asiento FROM CONTA_DINAMICA_ASIENTO;
	
FOR rec_1 in c1
LOOP


	SELECT id_dinamica
  into STRICT l_id_dinamica
  FROM conta_dinamica
	WHERE ID_ENTIDAD = v_ID_ENTIDAD
	AND ID_DEPTO = v_ID_DEPTO
	AND ID_ANHO = 2019
  AND nombre like 'Pagos a Proveedores';
	
	v_d_a_stmt_str := 'SELECT * FROM CONTA_DINAMICA_ASIENTO WHERE id_dinamica = :j and nro_asiento != 1  order by ID_ASIENTO';
	OPEN v_d_a_cursor FOR EXECUTE EXECUTE v_d_a_stmt_str USING rec_1.ID_DINAMICA;
		LOOP
	    FETCH v_d_a_cursor INTO d_a_record;
      EXIT WHEN NOT FOUND;/* apply on v_d_a_cursor */
	    l_id_asiento := l_id_asiento +1;
	    INSERT INTO CONTA_DINAMICA_ASIENTO(ID_ASIENTO, ID_PARENT, ID_DINAMICA, ID_TIPOPLAN,
	    				ID_RESTRICCION, ID_CUENTAAASI, ID_CUENTAEMPRESARIAL,
	    				NOMBRE, DC, PORCENTAJE, NRO_ASIENTO,
	    				ACTIVO, DESTINO, ID_INDICADOR,
	    				UNICO, UNICO_CTACTE, AGRUPA, ID_FONDO)
	    			VALUES (l_id_asiento, d_a_record.ID_PARENT, l_id_dinamica, d_a_record.ID_TIPOPLAN, 
	    				d_a_record.ID_RESTRICCION, d_a_record.ID_CUENTAAASI, d_a_record.ID_CUENTAEMPRESARIAL,
	    				d_a_record.NOMBRE, d_a_record.DC, d_a_record.PORCENTAJE, d_a_record.NRO_ASIENTO,
	    				d_a_record.ACTIVO, d_a_record.DESTINO, d_a_record.ID_INDICADOR,
	    				d_a_record.UNICO, d_a_record.UNICO_CTACTE, d_a_record.AGRUPA, d_a_record.ID_FONDO);
			v_d_cc_stmt_str := 'SELECT * FROM CONTA_DINAMICA_CTA_CTE WHERE id_asiento = :j order by ID_ASIENTO';

			OPEN v_d_cc_cursor FOR EXECUTE EXECUTE v_d_cc_stmt_str USING d_a_record.ID_ASIENTO;
				LOOP
			    FETCH v_d_cc_cursor INTO d_cc_record;
		    	EXIT WHEN NOT FOUND;/* apply on v_d_cc_cursor */
          RAISE NOTICE 'd_cc_record.ID_CTACTE';
          RAISE NOTICE '%', d_cc_record.ID_CTACTE;
			    INSERT INTO CONTA_DINAMICA_CTA_CTE(ID_ASIENTO, ID_ENTIDAD, ID_CTACTE)
			    			VALUES (l_id_asiento, v_ID_ENTIDAD, d_cc_record.ID_CTACTE);
		    END LOOP;
			CLOSE v_d_cc_cursor;	

			v_d_d_stmt_str := 'SELECT * FROM CONTA_DINAMICA_DEPTO WHERE id_asiento = :j';
			OPEN v_d_d_cursor FOR EXECUTE EXECUTE v_d_d_stmt_str USING d_a_record.ID_ASIENTO;
				LOOP
			    FETCH v_d_d_cursor INTO d_d_record;
		    	EXIT WHEN NOT FOUND;/* apply on v_d_d_cursor */
			    INSERT INTO CONTA_DINAMICA_DEPTO(ID_ASIENTO, ID_ENTIDAD, ID_DEPTO)
			    			VALUES (l_id_asiento, v_ID_ENTIDAD, d_d_record.ID_DEPTO);
		    END LOOP;
			CLOSE v_d_d_cursor;	
			
    END LOOP;
	CLOSE v_d_a_cursor;
END LOOP;

	
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE eliseo.scp_dinamic_create_adds ( v_ID_ENTIDAD bigint, v_ID_DEPTO text DEFAULT NULL) FROM PUBLIC;

