CREATE OR REPLACE PROCEDURE eliseo.spc_sta_ayudas_ingresos_rt ( p_id_entidad integer, p_id_entidad_assinet integer, p_id_anho integer, p_id_mes integer, p_id_persona integer, p_id_ctacte text, OUT CURSOR REFCURSOR ) AS $body$
DECLARE


    l_ingresos decimal(38,2);
    l_codigo_assinet varchar(20):='';


BEGIN
    DELETE FROM TT_TABLE_AUX;

	SELECT MAX(CODIGO_ASSINET) INTO STRICT l_codigo_assinet
  	FROM moises.PERSONA_NATURAL pn WHERE ID_PERSONA = p_id_persona;

    OPEN CURSOR FOR 
    SELECT to_char(A.fec_pago, 'DD/MM/YY') as fecha,
        b.NOMBRE AS COMENTARIO,
        A.COS_VALOR as valor FROM VW_APS_PLANILLA A 
        INNER JOIN APS_CONCEPTO_PLANILLA B ON A.ID_CONCEPTOAPS = B.ID_CONCEPTOAPS 
		WHERE ID_ENTIDAD = p_id_entidad
		AND ID_ANHO = p_id_anho
		AND ID_MES = p_id_mes
		AND ID_PERSONA = p_id_persona
		AND ID_TIPOPLANILLA in (98634,106791) -- 
		AND ID_TIPOCONCEPTOAPS IN (85) 
		
		
union

        SELECT 
        to_char(FEC_ASIENTO, 'DD/MMYY') AS FECHA,
            COMENTARIO,
            ABS(COS_VALOR) as valor
        from VW_CONTA_DIARIO
        where id_entidad = p_id_entidad_assinet
        and ID_CUENTAAASI = '1135005'
        and (ID_CTACTE = p_id_ctacte OR ID_CTACTE = l_codigo_assinet)
        and ID_MES = p_id_mes
        and ID_ANHO = p_id_anho
        AND NOT COMENTARIO LIKE 'APS%'
        AND DEBE=0 
;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
