CREATE OR REPLACE PROCEDURE eliseo.spc_user_session_login_app ( p_id_persona text, p_token text, p_caduca text, p_error OUT bigint ) AS $body$
DECLARE

  l_error bigint:=0;
  l_cant integer;

BEGIN
 
        
        select count(PER_ID) into STRICT l_cant
        from USERS_SESSION_ACADEMICO where token = p_token and status = '1';

        IF l_cant > 0 THEN
            update USERS_SESSION_ACADEMICO set LAST_UPDATE = current_timestamp where token = p_token and status = '1';
        ELSE
            insert into USERS_SESSION_ACADEMICO(TOKEN,PER_ID,DATE_CREATE,LAST_UPDATE,CADUCA,STATUS)
            values (p_token,p_id_persona,current_timestamp,current_timestamp,p_caduca,'1');
        END IF;

    p_error:=l_error;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
