CREATE OR REPLACE PROCEDURE eliseo.sp_anular_depositos_reg (P_ID_DEPOSITO bigint, P_ID_PERSONA bigint, P_MOTIVO text, P_ERROR OUT bigint,P_MSGERROR OUT text) AS $body$
DECLARE

        L_ID_VOUCHER bigint;
        L_ACTIVO varchar(1);
        L_ID_KARDEX bigint;
        L_ID_ALMACEN bigint;
        L_ID_ARTICULO bigint;
        L_ID_ANHO bigint;
        L_ID_TIPOORIGEN bigint := 1;
        L_ID_ASIENTO bigint;
        L_DATA varchar(255);

        L_ID_DEPOSITO bigint;
        L_ID_VOUCHER_DEP bigint;
        L_ID_TIPOORIGEN_DEP bigint;
        L_CANT bigint;
        L_ID_ARREGLO bigint;

        asientos_dep CURSOR FOR
        SELECT
                A.ID_ASIENTO
        FROM CONTA_ASIENTO A JOIN CAJA_DEPOSITO B
        ON A.ID_ORIGEN = B.ID_DEPOSITO
        WHERE A.ID_TIPOORIGEN = L_ID_TIPOORIGEN_DEP
        AND A.ID_ORIGEN = P_ID_DEPOSITO
        AND A.VOUCHER = L_ID_VOUCHER_DEP;


BEGIN
        -- VALIDAR SI EL VOUCHER ESTÃ CONTABILIZADO
        L_ACTIVO := 'S';

            SELECT V.ACTIVO INTO STRICT L_ACTIVO FROM CAJA_DEPOSITO D
            INNER JOIN CONTA_VOUCHER V ON D.ID_VOUCHER = V.ID_VOUCHER 
            WHERE D.ID_DEPOSITO = P_ID_DEPOSITO;

            IF L_ACTIVO = 'S' THEN
                --ANULAR DEPOSITO
                SELECT COUNT(1) INTO STRICT L_CANT FROM CAJA_DEPOSITO_DETALLE
                WHERE ID_DEPOSITO = P_ID_DEPOSITO;

                IF L_CANT >= 0 THEN

                    SELECT coalesce(MAX(ID_ARREGLO),0)+1  INTO STRICT L_ID_ARREGLO FROM ARREGLO;

                    -- Insertando en arreglo:
                    Insert into ARREGLO(ID_ARREGLO,
                    ID_ENTIDAD, ID_DEPTO, ID_TIPOARREGLO, ID_PERSONA, ID_MODULO, ID_ORIGEN, ID_TIPOORIGEN,
                    MOTIVO, FECHA, INFO_BACKUP, ESTADO
                    ) 
                    SELECT L_ID_ARREGLO, ID_ENTIDAD, id_depto, 1 as id_tipoarreglo, P_ID_PERSONA, 62, id_deposito, id_tipoorigen, P_MOTIVO, clock_timestamp(), 
                    'IMPORTE: '||importe||' Glosa: '||glosa, 2
                    from caja_deposito 
                    where id_deposito = P_ID_DEPOSITO;

                    SELECT ID_VOUCHER,ID_TIPOORIGEN INTO STRICT L_ID_VOUCHER_DEP,L_ID_TIPOORIGEN_DEP
                    FROM CAJA_DEPOSITO
                    WHERE ID_DEPOSITO = P_ID_DEPOSITO;

                    OPEN asientos_dep;
                      FETCH asientos_dep INTO L_ID_ASIENTO;
                        WHILE asientos_dep%FOUND LOOP

                            UPDATE CONTA_ASIENTO SET IMPORTE = 0, DESCRIPCION = '<< Anulado>>', IMPORTE_ME = 0
                            WHERE ID_ASIENTO = L_ID_ASIENTO;

                            FETCH asientos_dep INTO L_ID_ASIENTO;
                        END LOOP;
                    CLOSE asientos_dep;

                    UPDATE CAJA_DEPOSITO_DETALLE SET IMPORTE = 0, IMPORTE_ME = 0
                    WHERE ID_DEPOSITO = P_ID_DEPOSITO;

                    UPDATE CAJA_DEPOSITO SET IMPORTE = 0, IMPORTE_ME = 0, GLOSA = '<< Anulado >>'
                    WHERE ID_DEPOSITO = P_ID_DEPOSITO;
                END IF;
                P_ERROR := 0;
                P_MSGERROR := 'OK: Anulado con Exito';
            ELSE
                P_ERROR := 1;
                P_MSGERROR := 'El Voucher ya esta CONTABILIZADO';
            END IF;
    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
