DROP TRIGGER IF EXISTS tr_informe_numero ON informe CASCADE;
CREATE OR REPLACE FUNCTION eliseo.trigger_fct_tr_informe_numero() RETURNS trigger AS $BODY$
BEGIN
  SELECT coalesce(MAX((NUMERO)::numeric ),0)+1
  INTO STRICT NEW.NUMERO
  FROM ELISEO.INFORME
 	WHERE ID_ENTIDAD=NEW.ID_ENTIDAD AND ID_DEPTO=NEW.ID_DEPTO AND ID_ANHO=NEW.ID_ANHO;
RETURN NEW;
END
$BODY$
