DROP TRIGGER IF EXISTS tr_inventario_stock_articulo ON inventario_almacen_articulo CASCADE;
CREATE OR REPLACE FUNCTION eliseo.trigger_fct_tr_inventario_stock_articulo() RETURNS trigger AS $BODY$
DECLARE
L_CANTIDAD decimal(16,4) :=0;
L_COSTO decimal(16,4) :=0;
L_COSTO_TOTAL decimal(16,4) :=0;
BEGIN

        SELECT coalesce(SUM(CANTIDAD),0),coalesce(SUM(COSTO_TOTAL),0), coalesce(round((CASE WHEN SUM(CANTIDAD)=0 THEN 0  ELSE SUM(COSTO_TOTAL)/SUM(CANTIDAD) END )::numeric,4),0)
                INTO STRICT L_CANTIDAD,L_COSTO_TOTAL,L_COSTO
        FROM INVENTARIO_KARDEX
        WHERE ID_ANHO = OLD.ID_ANHO
        AND ID_ALMACEN = OLD.ID_ALMACEN
        AND ID_ARTICULO = OLD.ID_ARTICULO;

        IF L_COSTO <= 0 THEN
            NEW.COSTO := OLD.COSTO;
        ELSE
            NEW.COSTO := L_COSTO;
        END IF;

        IF L_CANTIDAD = 0 THEN
            L_COSTO_TOTAL := 0;
        END IF;

        NEW.STOCK_ACTUAL := L_CANTIDAD;
        NEW.COSTO_TOTAL := L_COSTO_TOTAL;
RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
