SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION        FC_BDV_PROYECTADO(P_ID_ENTIDAD NUMBER, P_ID_PERSONA NUMBER, P_ID_ANHO NUMBER) RETURN NUMBER
IS
contar_temp int:=0;
total_meses number(10,2):=0.00;
amount_temp number(10,2):=0.00;

BEGIN
	FOR mes IN 1..12
	LOOP
		SELECT count(*) into contar_temp 
		FROM  APS_PLANILLA_DETALLE WHERE ID_ANHO = P_ID_ANHO AND ID_CONCEPTOAPS = 1086 AND ID_MES=mes AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_PERSONA = P_ID_PERSONA; 
		IF contar_temp > 0 THEN
			SELECT SUM(COS_VALOR) INTO amount_temp 
			FROM  APS_PLANILLA_DETALLE WHERE ID_ANHO = P_ID_ANHO AND ID_CONCEPTOAPS = 1086 AND ID_MES=mes AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_PERSONA = P_ID_PERSONA;
		END IF;

		total_meses := total_meses + amount_temp;
	END LOOP;
	RETURN (total_meses);
END;

