SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

function        FC_CAJA_DEPOSITO_VENTA(P_ID_DEPOSITO INT) RETURN VARCHAR2
IS
s_venta varchar2(20);
s_resul varchar2(1000);
p_conta int;

CURSOR DEPOSITO_VENTA is
SELECT C.SERIE||'-'||C.NUMERO AS NUMERO
FROM CAJA_DEPOSITO A, CAJA_DEPOSITO_DETALLE B, VENTA C
WHERE A.ID_DEPOSITO = B.ID_DEPOSITO
AND B.ID_VENTA = C.ID_VENTA
AND A.ID_DEPOSITO = P_ID_DEPOSITO
AND A.ESTADO = '1'
GROUP BY C.SERIE, C.NUMERO;


BEGIN
    p_conta:=1;
    open DEPOSITO_VENTA;
        fetch DEPOSITO_VENTA into
            s_venta;
        while DEPOSITO_VENTA%found loop
            if p_conta = 1 then
                s_resul := s_venta;
            else
                s_resul := s_resul||', '||s_venta;
            end if;
                    
            fetch DEPOSITO_VENTA into
                s_venta;
            p_conta:=p_conta+1;
        end loop;
	close DEPOSITO_VENTA;	
	
	RETURN(s_resul);
END;

