SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION        FC_CONTA_CUO_COMPLETO(P_ID_TIPOORIGEN NUMBER, P_ID_MOV NUMBER, p_id_entidad NUMBER, p_id_anho NUMBER, p_id_mes number) RETURN VARCHAR2 AS
L_NOMBRE VARCHAR2(100);
BEGIN
	SELECT max(lote)||'-'||max('M'||d.NUM_AASI) INTO L_NOMBRE FROM VW_CONTA_DIARIO d
	WHERE trim(to_char(P_ID_TIPOORIGEN,'999'))||'-'||trim(to_char(P_ID_MOV,'9999999999999999999')) = d.OBSERVACION
	AND d.ID_ENTIDAD = p_id_entidad
	AND d.ID_ANHO = p_id_anho
	AND d.ID_MES = p_id_mes
	AND d.ID_CUENTAAASI IN ( 2130101,2130103,2130107) ;
  RETURN(L_NOMBRE);        
END;

