SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION FC_DEPTO_BY_FUNCTION(P_ID_COMPRA in number) RETURN varchar2 IS
   p_return		  		   varchar2(125);
BEGIN

     p_return := '';

        SELECT COALESCE(max(pdg.ID_DEPTO),'') into p_return  FROM PEDIDO_COMPRA pc 
        INNER JOIN PEDIDO_DIST_GASTO pdg ON pdg.ID_PEDIDO = pc.ID_PEDIDO 
        WHERE pc.ID_COMPRA = P_ID_COMPRA;

	 RETURN( p_return );
END;

