SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

function FC_DINAMICA_ASIENTO_DEPTO(P_ID_ASIENTO INT) RETURN VARCHAR2
IS
s_depto varchar2(20);
s_resul varchar2(1000);
p_conta int;

CURSOR ASIENTO_DEPTO is
SELECT ID_DEPTO 
FROM CONTA_DINAMICA_DEPTO
WHERE ID_ASIENTO = P_ID_ASIENTO 
ORDER BY ID_DEPTO;


BEGIN
    p_conta:=1;
    open ASIENTO_DEPTO;
        fetch ASIENTO_DEPTO into
            s_depto;
        while ASIENTO_DEPTO%found loop
            if p_conta = 1 then
                s_resul := s_depto;
            else
                s_resul := s_resul||', '||s_depto;
            end if;
                    
            fetch ASIENTO_DEPTO into
                s_depto;
            p_conta:=p_conta+1;
        end loop;
	close ASIENTO_DEPTO;	
	
	RETURN(s_resul);
END;

