SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION               FC_EMAIL(P_ID_PERSONA NUMBER) RETURN VARCHAR2 IS
    L_EMAIL VARCHAR2(80);
    BEGIN 
       
   		select case when correo = 'N' then '' else correo end INTO L_EMAIL
		from moises.persona_natural where ID_PERSONA=P_ID_PERSONA and rownum=1;
		IF L_EMAIL IS NULL THEN 
			SELECT
			        NVL(max(DIRECCION),'') INTO L_EMAIL
			FROM MOISES.PERSONA_VIRTUAL
			WHERE ID_PERSONA = P_ID_PERSONA
			AND ES_ACTIVO=1 and rownum=1;
		END IF;
    RETURN(L_EMAIL); 
END;

