SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION        FC_EMPRESA_RUC(P_ID_ENTIDAD NUMBER) RETURN VARCHAR2 IS
    L_NOMBRE VARCHAR2(100);
    BEGIN 
        /*
        1-RAZON SOCIAL
        2-NOMBRE COMERCIAL
        3-NOMBRE DENOMINACIONAL */
    SELECT
            NVL(B.ID_RUC,'') INTO L_NOMBRE
    FROM CONTA_ENTIDAD A, CONTA_EMPRESA B, PERSONA_JURIDICA C, PERSONA D
    WHERE A.ID_EMPRESA = B.ID_EMPRESA
    AND B.ID_RUC = C.ID_RUC
    AND C.ID_PERSONA = D.ID_PERSONA
    AND A.ID_ENTIDAD = P_ID_ENTIDAD;
    RETURN(L_NOMBRE); 
END;

