SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION FC_FORMATO_IMPORTE (P_IMPORTE NUMBER)
   RETURN VARCHAR2
IS
   L_IMPORTE   VARCHAR2 (50);
BEGIN
   SELECT TO_CHAR(NVL((
             CASE
                WHEN P_IMPORTE = 0
                THEN
                   '0.00'
                WHEN P_IMPORTE BETWEEN 0.01 AND 0.99
                THEN
                   TRIM (TO_CHAR (P_IMPORTE, '90D99'))
                ELSE
                   LTRIM (TO_CHAR (P_IMPORTE, '9999999999D99'))         
             END
             ),'fm999999999990.00')) INTO L_IMPORTE
    FROM DUAL;
    RETURN(P_IMPORTE);
END;

