FUNCTION        FC_GERARQUIA(P_ID_MODULO   VARCHAR2 ) 
RETURN VARCHAR2 IS V_ANSWER VARCHAR2(200);

BEGIN 

select SYS_CONNECT_BY_PATH(nombre,' > ') into V_ANSWER  from lamb_modulo 
where id_padre is null
