\set ON_ERROR_STOP ON

function FC_GTH_OBTENER_CELULAR(P_ID_PERSONA NUMBER) RETURN VARCHAR2
IS
l_dato VARCHAR2(100):='';
l_contar int:=0;
l_id number;

BEGIN
  
   select COUNT(*) INTO l_contar from moises.persona_TELEFONO WHERE ID_PERSONA=P_ID_PERSONA and ID_TIPOTELEFONO in (3,5) and GTH=1;
   
   IF l_contar>0 THEN
     IF l_contar=1 THEN
      select NUM_TELEFONO INTO l_dato from moises.persona_TELEFONO WHERE ID_PERSONA=P_ID_PERSONA and ID_TIPOTELEFONO in (3,5) and GTH=1;
     ELSE
      select max(id_TELEFONO) into l_id from moises.persona_TELEFONO WHERE ID_PERSONA=P_ID_PERSONA and ID_TIPOTELEFONO in (3,5) and GTH=1;
      select NUM_TELEFONO INTO l_dato from moises.persona_TELEFONO WHERE ID_PERSONA=P_ID_PERSONA and id_TELEFONO=l_id and ID_TIPOTELEFONO in (3,5) and GTH=1;
     END IF;
   END IF;
  
	RETURN(l_dato);
END;

