SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

function        FC_GTH_OBTENER_EMAIL(P_ID_PERSONA NUMBER) RETURN VARCHAR2
IS
l_dato VARCHAR2(100):='';
l_contar int:=0;
l_id_virtual number;

BEGIN
  
   select COUNT(*) INTO l_contar from MOISES.persona_virtual WHERE ID_PERSONA=P_ID_PERSONA and ID_TIPOVIRTUAL=1 and GTH=1;
   
   IF l_contar>0 THEN
     IF l_contar=1 THEN
      select DIRECCION INTO l_dato from MOISES.persona_virtual WHERE ID_PERSONA=P_ID_PERSONA and ID_TIPOVIRTUAL=1 and GTH=1;
     ELSE
      select max(id_virtual) into l_id_virtual from MOISES.persona_virtual WHERE ID_PERSONA=P_ID_PERSONA and ID_TIPOVIRTUAL=1 and GTH=1;
      select DIRECCION INTO l_dato from MOISES.persona_virtual WHERE ID_PERSONA=P_ID_PERSONA and id_virtual=l_id_virtual and ID_TIPOVIRTUAL=1 and GTH=1;
     END IF;
   END IF;
  
	RETURN(l_dato);
END;

