SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION        FC_MONEDA(P_ID_MONEDA IN NUMBER,P_OPTION IN NUMBER DEFAULT 0) RETURN VARCHAR2 IS
--P_OPTION ------> 0=NOMBRE; 1=NOMBRE PLURAL; 2=SIGLAS; 3 = SIMBOLO
    r_dato  varchar2(200):='';
   	l_nombre varchar2(50);
  	l_nombre_plural varchar2(50);
  	l_simbolo varchar2(50);
  	l_siglas varchar2(50);
    BEGIN

	    SELECT A.NOMBRE,A.NOMBRE_PLURAL,A.SIMBOLO,A.SIGLAS INTO l_nombre,l_nombre_plural,l_simbolo,l_siglas 
	    FROM ELISEO.CONTA_MONEDA A 
		WHERE A.ID_MONEDA = P_ID_MONEDA
		AND A.ESTADO = '1' AND ROWNUM=1;

	IF P_OPTION = 0 THEN 
		r_dato := l_nombre;
	ELSIF P_OPTION = 1 THEN 
		r_dato := l_nombre_plural;
	ELSIF P_OPTION = 2 THEN 
		r_dato := l_siglas;
	ELSIF P_OPTION = 3 THEN 
		r_dato := l_simbolo;
	ELSE 
		r_dato := '';
	END IF;

	 RETURN( trim(r_dato) );
END;

