SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION FC_NAMESDEPTO(COD_ENTIDA in varchar2,ID_DEPT in varchar2) RETURN varchar2 IS
   p_name		  		   varchar2(125);
   cursor datos is
       Select
	       a.nombre
	   from CONTA_ENTIDAD_DEPTO a
	   where a.ID_ENTIDAD = COD_ENTIDA
     AND a.ID_DEPTO=ID_DEPT
     and a.ES_ACTIVO=1;
BEGIN
	 open datos;
	 fetch datos into p_name;
	 if not datos%found then
	   p_name := '';
	 end if;
	 close datos;
	 RETURN( p_name );
END;

