FUNCTION FC_NOMBRE_FAMILIA(P_CODIGO VARCHAR2) RETURN VARCHAR2 IS
    L_NOMBRE VARCHAR2(200);
    BEGIN 
    SELECT 
            NOMBRE INTO L_NOMBRE
    FROM INVENTARIO_ARTICULO
    WHERE CODIGO = P_CODIGO;
    RETURN(L_NOMBRE); 
END;

