FUNCTION FC_OBTENER_SP_TASA(P_ID_SISTEMAPENSION NUMBER, P_ID_ANHO NUMBER, P_ID_MES NUMBER) RETURN VARCHAR2
IS
l_dato number(10,2):=0.00;

BEGIN
	SELECT SUM(TASA) INTO l_dato 
	FROM APS_SISTEMA_PENSION_TASA 
	WHERE ID_SISTEMAPENSION = P_ID_SISTEMAPENSION AND ID_ANHO = P_ID_ANHO AND ID_MES = P_ID_MES;

	RETURN(l_dato);
END;

