SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION        FC_OBTENER_SP(P_ID_CONTRATO NUMBER, P_ID_PERSONA NUMBER, P_ID_ENTIDAD NUMBER) RETURN VARCHAR2
IS
l_dato NUMBER;
l_contar int:=0;
l_id_virtual number;
anho number;
mes number;

BEGIN
   select COUNT(*) INTO l_contar from APS_PLANILLA WHERE ID_CONTRATO=P_ID_CONTRATO;
   
   IF l_contar>0 THEN
     IF l_contar=1 THEN
      SELECT ID_SISTEMAPENSION INTO l_dato FROM APS_PLANILLA WHERE ID_CONTRATO=P_ID_CONTRATO AND ID_PERSONA = P_ID_PERSONA AND ID_ENTIDAD = P_ID_ENTIDAD;
     ELSE
		
			SELECT SUBSTR((SELECT "MAX"("CONCAT"(ID_ANHO, ID_MES)) FROM APS_PLANILLA WHERE APS_PLANILLA.ID_CONTRATO = P_ID_CONTRATO 
			AND APS_PLANILLA.ID_PERSONA = P_ID_PERSONA AND APS_PLANILLA.ID_ENTIDAD = P_ID_ENTIDAD),0,4) "Substring" into anho
			FROM DUAL;

			SELECT SUBSTR((SELECT "MAX"("CONCAT"(ID_ANHO, ID_MES)) FROM APS_PLANILLA WHERE APS_PLANILLA.ID_CONTRATO = P_ID_CONTRATO 
			AND APS_PLANILLA.ID_PERSONA = P_ID_PERSONA AND APS_PLANILLA.ID_ENTIDAD = P_ID_ENTIDAD),5,2) "Substring" INTO mes
			FROM DUAL;

			SELECT ID_SISTEMAPENSION INTO l_dato FROM APS_PLANILLA WHERE APS_PLANILLA.ID_CONTRATO = P_ID_CONTRATO AND ID_PERSONA = P_ID_PERSONA 
				AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_ANHO = anho AND ID_MES = mes;
     END IF;
   END IF;
	RETURN(l_dato);
END;

