SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

function        FC_OBT_NAME_ENTIDAD_DEPTO(P_ID_ENTIDAD NUMBER, P_ID_DEPTO VARCHAR2) RETURN VARCHAR2
IS
l_dato VARCHAR2(150):='';
l_contar int:=0;
l_id_virtual number;

BEGIN

	IF (P_ID_DEPTO IS NULL OR P_ID_DEPTO = ' ' OR P_ID_DEPTO = 0)  THEN
			Select NOM_ENTIDAD INTO l_dato from VW_CONTA_ENTIDAD where ID_ENTIDAD = P_ID_ENTIDAD;
	ELSE
			Select VWCE.NOMBRE || ' - ' || VWCED.NOM_DEPARTAMENTO INTO l_dato from VW_CONTA_ENTIDAD VWCE
			INNER JOIN VW_CONTA_ENTIDAD_DEPTO VWCED ON VWCE.ID_ENTIDAD = VWCED.ID_ENTIDAD where VWCED.ID_ENTIDAD = P_ID_ENTIDAD AND VWCED.ID_DEPTO = P_ID_DEPTO;

	END IF;
	RETURN(l_dato);
END;

