SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION        FC_PEDIDO_ASIENTO_MATCH(in_id_pedido IN NUMBER) RETURN VARCHAR2 IS 
    v_result varchar2(1000);
    v_sumad decimal(10,2);
    v_sumac decimal(10,2);
BEGIN
    SELECT
      (SELECT NVL(SUM(PORCENTAJE),0)
        FROM PEDIDO_ASIENTO
        WHERE ID_PEDIDO = in_id_pedido AND DC = 'D')
      ,(SELECT NVL(SUM(PORCENTAJE),0)
        FROM PEDIDO_ASIENTO
        WHERE ID_PEDIDO = in_id_pedido AND DC = 'C')
      INTO
      v_sumad
      ,v_sumac
    FROM DUAL;
    -- 
    IF v_sumad != v_sumac THEN
        v_result := 'NO';
    ELSIF v_sumad != 100 THEN
        IF v_sumad/2 = 100 AND v_sumac/2 = 100 THEN
            v_result := 'SI';
        ELSE
            v_result := 'NO';
        END IF;
    ELSE
        v_result := 'SI';
    END IF;
    RETURN (v_result);
END ;

