SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION fc_proveedor_rn( p_idorigen IN NUMBER) RETURN VARCHAR2 IS
  p_nombre VARCHAR2(200);
  CURSOR dato  IS
    SELECT b.empresa
    FROM eliseo.COMPRA a
    JOIN
      (SELECT b.ID_RUC||' '||a.NOMBRE empresa,
        b.ID_PERSONA
      FROM eliseo.PERSONA a
      JOIN eliseo.PERSONA_JURIDICA b
      ON a.ID_PERSONA      =b.ID_PERSONA
      ) b ON a.ID_PROVEEDOR=b.ID_PERSONA
  WHERE a.id_compra        = p_idorigen;
BEGIN
  OPEN dato;
  FETCH dato INTO p_nombre;
  IF NOT dato%found THEN
    p_nombre := ' ';
  END IF;
CLOSE dato;
RETURN(p_nombre);
END;

