SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION        FC_PROVISION_GRATI(P_ID_ENTIDAD NUMBER, P_ID_PERSONA NUMBER, P_ANHO NUMBER, P_MES NUMBER) RETURN NUMBER
IS
l_valor number(10,2):=0.00;
l_contar int:=0;
BEGIN
		SELECT COUNT(*) INTO l_contar FROM APS_PLANILLA_DETALLE 
		WHERE ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 9100 AND 
			ID_PERSONA = P_ID_PERSONA AND ID_ANHO = P_ANHO AND ID_MES=P_MES;

	IF l_contar >0 THEN
		IF l_contar = 1 THEN
				SELECT COS_VALOR INTO l_valor FROM APS_PLANILLA_DETALLE 
				WHERE ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 9100 AND 
					ID_PERSONA = P_ID_PERSONA AND ID_ANHO = P_ANHO AND ID_MES=P_MES;
		ELSE
				SELECT AVG(COS_VALOR) INTO l_valor FROM APS_PLANILLA_DETALLE 
				WHERE ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 9100 AND 
					ID_PERSONA = P_ID_PERSONA AND ID_ANHO = P_ANHO AND ID_MES=P_MES;
		END IF;
	END IF;
	RETURN (l_valor);
END;

