SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

function        FC_PSTO_COND_LABORAL(P_ID_CARGO_PROCESO NUMBER) RETURN VARCHAR2
IS
l_dato varchar2(3000):='';
l_nombre varchar2(50);
p_conta int;

CURSOR CUR is
SELECT A.ID_COND_LAB 
FROM PSTO_PLLA_CARGO_PROCESO_CONLAB A,APS_CONDICION_LABORAL B
WHERE A.ID_COND_LAB = B.ID_COND_LAB
AND A.ID_CARGO_PROCESO=P_ID_CARGO_PROCESO
ORDER BY B.ID_COND_LAB;


BEGIN
    p_conta:=1;
    open CUR;
        fetch CUR into l_nombre;
        while CUR%found loop
            if p_conta = 1 then
                l_dato := l_nombre;
            else
                l_dato := l_dato||'|'||l_nombre;
            end if;
                    
            fetch CUR into l_nombre;
            p_conta:=p_conta+1;
        end loop;
	close CUR;	
	
	RETURN(l_dato);
END;

