SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION FC_PSTO_MES_NOMBRE (P_ID_MES in NUMBER,P_TIPO VARCHAR2) RETURN varchar2 IS
   l_mes_nombre varchar(30):='-';
   l_contar number;
BEGIN
	 
   SELECT COUNT(*) INTO l_contar FROM CONTA_MES 
   WHERE ID_MES=P_ID_MES;
	 
   IF l_contar=1 THEN
      SELECT CASE WHEN  P_TIPO='L' THEN NOMBRE ELSE SIGLAS END INTO l_mes_nombre FROM CONTA_MES 
      WHERE ID_MES=P_ID_MES;
   END IF;
   RETURN( l_mes_nombre );
END;

