SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION        FC_REMUN_VARIABLE(P_ID_ENTIDAD NUMBER, P_ID_PERSONA NUMBER, FECHA_INI DATE, FECHA_FIN DATE) RETURN NUMBER
IS
l_monto number(10,2):=0.00;
l_contar int:=0;
BEGIN
	
	IF EXTRACT(YEAR FROM FECHA_INI) = EXTRACT(YEAR FROM FECHA_FIN) THEN
		--CALCULA ENTRE DOS FECHAS DEL MISMO AÑO
		SELECT COUNT(*) INTO l_contar FROM APS_PLANILLA_DETALLE 
		WHERE ID_MES BETWEEN EXTRACT(MONTH FROM FECHA_INI) AND EXTRACT(MONTH FROM FECHA_FIN) AND ID_ANHO = EXTRACT(YEAR FROM FECHA_INI)
			AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1086 AND ID_PERSONA = P_ID_PERSONA;
		IF l_contar >= 3 THEN
			SELECT AVG(COS_VALOR) INTO l_monto FROM  APS_PLANILLA_DETALLE 
			WHERE ID_MES BETWEEN EXTRACT(MONTH FROM FECHA_INI) AND EXTRACT(MONTH FROM FECHA_FIN) AND ID_ANHO = EXTRACT(YEAR FROM FECHA_INI)
				AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1086 AND ID_PERSONA = P_ID_PERSONA;
		END IF;
	ELSE
		--CALCULA ENTRE DOS FECHAS DE DIFERENTES AÑOS
		SELECT COUNT(*) INTO l_contar FROM APS_PLANILLA_DETALLE 
		WHERE ((ID_MES >=EXTRACT(MONTH FROM FECHA_INI) AND ID_ANHO=EXTRACT(YEAR FROM FECHA_INI)) OR (ID_MES <= EXTRACT(MONTH FROM FECHA_FIN) AND ID_ANHO=EXTRACT(YEAR FROM FECHA_FIN))) 
			AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1086 AND ID_PERSONA = P_ID_PERSONA;
		IF l_contar >= 3 THEN
			SELECT AVG(COS_VALOR) INTO l_monto FROM  APS_PLANILLA_DETALLE 
			WHERE ((ID_MES >=EXTRACT(MONTH FROM FECHA_INI) AND ID_ANHO=EXTRACT(YEAR FROM FECHA_INI)) OR (ID_MES <= EXTRACT(MONTH FROM FECHA_FIN) AND ID_ANHO=EXTRACT(YEAR FROM FECHA_FIN))) 
				AND ID_ENTIDAD = P_ID_ENTIDAD AND ID_CONCEPTOAPS = 1086 AND ID_PERSONA = P_ID_PERSONA;
		END IF;
	END IF;
RETURN (l_monto);
END;

