FUNCTION FC_SIGLA_CTA_BANCARIA(P_ID_CTABANCARIA IN NUMBER) RETURN VARCHAR2 IS
        L_SIGLA VARCHAR2(255);
        BEGIN
            SELECT NVL(A.SIGLA,'') INTO L_SIGLA
            FROM CAJA_ENTIDAD_FINANCIERA A JOIN CAJA_CUENTA_BANCARIA B ON A.ID_BANCO = B.ID_BANCO
            WHERE B.ID_CTABANCARIA = P_ID_CTABANCARIA;
            
            RETURN (L_SIGLA);
    END;

