SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION FC_TIPOCAMBIO(P_ID_MONEDA NUMBER,P_FECHA date,P_TIPO VARCHAR2) RETURN number AS
  l_tc number(10,3);
  cursor datos is 
      select coalesce(case when P_TIPO='C' then cos_compra
                    when P_TIPO='V' then cos_venta 
               else 
                cos_denominacional 
               end,0) as tc
        from TIPO_CAMBIO 
        where id_moneda= P_ID_MONEDA 
        and to_char(fecha,'YYYYMMDD')=to_char(P_FECHA,'YYYYMMDD');
BEGIN
        
      open datos;
       fetch datos into l_tc;
       if not datos%found then
         l_tc := 0;
       end if;
       close datos;
       RETURN( l_tc );
          
END;

