FUNCTION        FC_VEHICULO_PRECIO(P_ID_VEHICULO NUMBER) RETURN NUMBER IS
    L_PRECIO NUMBER(10,2);
    BEGIN 
    SELECT
           NVL(KM_PRECIO,0)  INTO L_PRECIO
