SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION        FC_VEHICULO(P_ID_VEHICULO NUMBER) RETURN VARCHAR2 IS
    L_NOMBRE VARCHAR2(100);
    BEGIN 
        /*
        1-RAZON SOCIAL
        2-NOMBRE COMERCIAL
        3-NOMBRE DENOMINACIONAL */
    SELECT
           PLACA||' - '||MODELO  INTO L_NOMBRE
    FROM PEDIDO_VEHICULO
    WHERE ID_VEHICULO = P_ID_VEHICULO;
    RETURN(L_NOMBRE); 
END;

