PROCEDURE iudp_Asist_Marcaciones_Interno(
    v_ICODUSUARIO      IN NUMBER,
    v_ID_ENTIDAD       IN NUMBER,
    v_FECHA            IN DATE,
    v_HORARIOENTRADA   IN DATE,
    v_HORARIOSALIDA    IN DATE,
    v_INICIOREFRIGERIO IN DATE,
    v_FINREFRIGERIO    IN DATE )
AS
  v_Id_Persona  NUMBER(38,0);
  l_Id_Entidad  NUMBER(38,0);
  v_Result  NUMBER(10,0);
  v_Mensaje VARCHAR2(255);
  v_Extra   VARCHAR2(255);
BEGIN
  v_Result  := -1 ;
  v_Mensaje := 'Mensaje.' ;
  v_Extra   := 'Extra.' ;
  DECLARE
    v_temp NUMBER(1, 0) := 0;
  BEGIN
    v_Result := 0 ;
    SELECT  
					      max(NVL(Persona.ID_PERSONA, 0)) into v_Id_Persona
					    FROM PERSONA
					    LEFT JOIN Persona_Natural ON
					      Persona.ID_PERSONA = Persona_Natural.ID_PERSONA
					    LEFT JOIN Persona_Documento ON
					      Persona.ID_PERSONA = Persona_Documento.ID_PERSONA
					    WHERE Persona_Documento.NUM_DOCUMENTO = v_ICODUSUARIO;   
    select max(id_entidad) into l_Id_Entidad from vw_aps_empleado
    where id_persona = v_Id_Persona
    and v_FECHA between fec_inicio and coalesce(fec_termino, to_date('2030-12-31','yyyy-mm-dd'))
    ;              
    BEGIN
      SELECT 1
      INTO v_temp
      FROM DUAL
      WHERE NOT EXISTS
        (SELECT *
        FROM APS_ASIST_MARCACIONES_INTERNO
        WHERE ID_PERSONA = v_Id_Persona
        AND ID_ENTIDAD    = v_ID_ENTIDAD
        AND FECHA         = v_FECHA
        AND ROWNUM       <= 1
        );
    EXCEPTION
    WHEN OTHERS THEN
      NULL;
    END;
    IF v_temp = 1 THEN
      BEGIN
        INSERT
        INTO APS_ASIST_MARCACIONES_INTERNO
          (
            ID_PERSONA,
            ID_ENTIDAD,
            FECHA,
            HORARIOENTRADA,
            HORARIOSALIDA,
            INICIOREFRIGERIO,
            FINREFRIGERIO
          )
          VALUES
          (
            v_Id_Persona,
            l_Id_Entidad,
            v_FECHA,
            v_HORARIOENTRADA,
            v_HORARIOSALIDA,
            v_INICIOREFRIGERIO,
            v_FINREFRIGERIO
          );
      END;
    ELSE
      BEGIN
        UPDATE APS_ASIST_MARCACIONES_INTERNO
        SET HORARIOENTRADA = v_HORARIOENTRADA,
          HORARIOSALIDA    = v_HORARIOSALIDA,
          INICIOREFRIGERIO = v_INICIOREFRIGERIO,
          FINREFRIGERIO    = v_FINREFRIGERIO
        WHERE ID_PERSONA  = v_Id_Persona
        AND ID_ENTIDAD     = v_ID_ENTIDAD
        AND FECHA          = v_FECHA;--SELECT CODIGO = @Result, MENSAJE = @Mensaje, EXTRA = @Extra
      END;
    END IF;
  END;
EXCEPTION
WHEN OTHERS THEN
  utils.handleerror(SQLCODE,SQLERRM);
END;

