PROCEDURE        iudp_Entidad_Cuenta_Corriente (P_Modo int, P_ID_ENTIDAD int, P_ID_CUENTACTE VARCHAR, P_ID_MONEDA varchar2, P_ID_TIPOCUENTACTE varchar2, P_ID_TIPOCUENTABANCO INT, P_NOMBRE varchar2) AS
   S_MONEDA VARCHAR2(10);
   S_NOMBRE VARCHAR(255);
   S_CANT   INT;
BEGIN
    S_MONEDA := CASE WHEN P_ID_MONEDA IN ('@CurrencyCode@') THEN '' ELSE P_ID_MONEDA END;
	S_NOMBRE := CASE WHEN P_NOMBRE = '@Name@' THEN '' ELSE P_NOMBRE END;
    
    IF P_Modo = 0 THEN
                     
        SELECT COUNT(ID_TIPOCTACTE) INTO S_CANT 
        FROM Tipo_Cta_Corriente 
        WHERE ID_TIPOCTACTE = P_ID_TIPOCUENTACTE;
        
        IF S_CANT = 0 THEN
            INSERT INTO Tipo_Cta_Corriente (ID_TIPOCTACTE, NOMBRE)
            VALUES (P_ID_TIPOCUENTACTE, P_ID_TIPOCUENTACTE);
        END IF;
        
        SELECT COUNT(*) INTO S_CANT 
        FROM CONTA_ENTIDAD_CTA_CTE 
        WHERE ID_ENTIDAD = P_ID_ENTIDAD 
        AND ID_TIPOCTACTE = P_ID_TIPOCUENTACTE 
        AND ID_CTACTE = P_ID_CUENTACTE;
        
        IF S_CANT = 0 THEN
            INSERT INTO CONTA_ENTIDAD_CTA_CTE (ID_ENTIDAD, ID_TIPOCTACTE, ID_CTACTE, ID_TIPOCUENTABANCO, ID_MONEDA, NOMBRE)
            VALUES (P_ID_ENTIDAD, P_ID_TIPOCUENTACTE, P_ID_CUENTACTE, P_ID_TIPOCUENTABANCO, NVL(S_MONEDA, '7'), nvl(S_NOMBRE,'-'));
        ELSE                      
            UPDATE CONTA_ENTIDAD_CTA_CTE SET
                    ID_TIPOCUENTABANCO = NVL(P_ID_TIPOCUENTABANCO, ID_TIPOCUENTABANCO),
                    ID_MONEDA          = NVL(S_MONEDA, '7'),
                    NOMBRE             = nvl(S_NOMBRE,'-')
            WHERE ID_ENTIDAD = P_ID_ENTIDAD
            AND ID_TIPOCTACTE = P_ID_TIPOCUENTACTE
            AND ID_CTACTE = P_ID_CUENTACTE;
        END IF;
    END IF;
END;

