SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

PROCEDURE        iudp_Entidad_Periodo (P_Modo IN int, P_ID_ENTIDAD IN int, P_ID_ANHO IN int, P_ID_MES IN int, P_FEC_CIERRE IN varchar2, P_NOM_CIERRE IN varchar2) AS
 
  S_NOM_CIERRE           varchar2(100);
  S_CANTIDAD            INT;
  S_FECHA				DATE;
    
 BEGIN
	 
IF P_Modo = 0 THEN

    S_FECHA := CASE WHEN P_FEC_CIERRE IN ('@ClosedDate@') THEN NULL ELSE to_date(P_FEC_CIERRE,'yyyy-mm-dd"T"HH24:MI:SS') END;
    S_NOM_CIERRE := CASE WHEN P_NOM_CIERRE IN ('@ClosedBy@') THEN '' ELSE P_NOM_CIERRE END;
    
    
    SELECT COUNT(*) INTO S_CANTIDAD FROM CONTA_ANHO WHERE ID_ANHO= P_ID_ANHO; 
    
    IF S_CANTIDAD = 0 THEN
    
      INSERT INTO CONTA_ANHO (ID_ANHO)
        VALUES (P_ID_ANHO);
    
    END IF;    
    
    S_CANTIDAD := 0;
    
    SELECT COUNT(*) INTO S_CANTIDAD FROM CONTA_ENTIDAD_ANHO WHERE ID_ENTIDAD = P_ID_ENTIDAD AND ID_ANHO= P_ID_ANHO; 
    
    IF S_CANTIDAD = 0 THEN
    
      INSERT INTO CONTA_ENTIDAD_ANHO (ID_ENTIDAD, ID_ANHO)
        VALUES (P_ID_ENTIDAD, P_ID_ANHO);
    
    END IF; 
        
    S_CANTIDAD := 0;
    
    SELECT COUNT(*) INTO S_CANTIDAD FROM CONTA_ENTIDAD_PERIODO WHERE ID_ENTIDAD = P_ID_ENTIDAD AND ID_ANHO= P_ID_ANHO AND ID_MES = P_ID_MES; 
    
    IF S_CANTIDAD = 0 THEN    
        INSERT INTO CONTA_ENTIDAD_PERIODO (ID_ENTIDAD, ID_ANHO, ID_MES, FEC_CIERRE, NOM_CIERRE)
        VALUES (P_ID_ENTIDAD, P_ID_ANHO, P_ID_MES, S_FECHA, S_NOM_CIERRE);
        
    ELSE
      UPDATE CONTA_ENTIDAD_PERIODO SET
        FEC_CIERRE = S_FECHA,
        NOM_CIERRE = S_NOM_CIERRE
      WHERE ID_ENTIDAD = P_ID_ENTIDAD AND ID_ANHO= P_ID_ANHO AND ID_MES = P_ID_MES;
    END IF;  
  END IF;
   
 end;

