SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

PROCEDURE        iudp_Fondo (P_Modo IN int, P_ID_FONDO IN varchar2, P_ID_PARENT IN varchar2, P_NOMBRE IN varchar2) AS
 
  S_ID_FONDO            NUMBER;
  S_ID_PARENT           NUMBER;
  S_CANTIDAD            INT;
    
 BEGIN
	 
IF P_Modo = 0 THEN
    

    S_ID_FONDO :=  TO_NUMBER(P_ID_FONDO, '9999999');
    S_ID_PARENT := CASE WHEN P_ID_PARENT IN ('@ParentCode@','') THEN NULL ELSE TO_NUMBER(P_ID_PARENT, '9999999') END;
    
    SELECT COUNT(*) INTO S_CANTIDAD FROM Conta_Fondo WHERE ID_FONDO = P_ID_FONDO; 
    
    IF S_CANTIDAD = 0 THEN
    
      INSERT INTO Conta_Fondo (ID_FONDO, ID_PARENT, NOMBRE, ES_GRUPO)
        VALUES (S_ID_FONDO, S_ID_PARENT, P_NOMBRE, '0');
    
    ELSE
      UPDATE Conta_Fondo SET
        NOMBRE = P_NOMBRE
      WHERE ID_FONDO = S_ID_FONDO;
    END IF;  
    
    UPDATE Conta_Fondo SET
      ES_GRUPO = '1'
    WHERE ID_FONDO = S_ID_PARENT;
  END IF;

   
 end;

