SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

PROCEDURE        scp_conf_vouch_clone_by_year(
    v_ID_ENTIDAD         IN NUMBER,
    v_ID_YEAR_FROM    IN NUMBER,
    v_ID_YEAR_TO    IN NUMBER,
    v_ERROR OUT NUMBER,
    v_MSGERROR OUT VARCHAR2
)
AS
    L_ERROR NUMBER :=0;
    L_MSGERROR VARCHAR2(100) :='';
    l_contar number := 0;

BEGIN

    SELECT count(1) into l_contar FROM CONTA_VOUCHER_CONFIG
    WHERE ID_ANHO = v_ID_YEAR_TO
    AND ID_ENTIDAD = v_ID_ENTIDAD;

    IF l_contar > 0 THEN 
        L_ERROR := 1;
        L_MSGERROR := 'Alto: Ya existen configuraciones de vouchers en el a√±o : '||to_char(v_ID_YEAR_TO);
        goto salida_rapida;
    END IF;

    IF L_ERROR = 0 THEN 
        INSERT INTO CONTA_VOUCHER_CONFIG (ID_ENTIDAD, ID_DEPTO, ID_TIPOASIENTO, ID_ANHO, ID_MODULO, FECHA, AUTOMATICO,
            NOMBRE, ID_TIPOVOUCHER, ID_SISTEMAEXTERNO, ID_TIPOASIENTO_PARENT, ID_TIPOVOUCHER_PARENT )
        SELECT ID_ENTIDAD, ID_DEPTO, ID_TIPOASIENTO, v_ID_YEAR_TO, ID_MODULO, FECHA, AUTOMATICO,
        NOMBRE, ID_TIPOVOUCHER, ID_SISTEMAEXTERNO, ID_TIPOASIENTO_PARENT, ID_TIPOVOUCHER_PARENT 
        FROM CONTA_VOUCHER_CONFIG 
        WHERE 
        ID_ANHO=v_ID_YEAR_FROM
        AND ID_ENTIDAD = v_ID_ENTIDAD;
    END IF;

    <<salida_rapida>>
    V_ERROR := L_ERROR;
    V_MSGERROR := L_MSGERROR;
END;

